INSERT INTO `hitokoto` VALUES (4985, '眼睛为她下着雨，心却为她打着伞，这就是爱情。 ', 4, '2020-02-12 16:36:00', '2020-04-23 16:11:07');
INSERT INTO `hitokoto` VALUES (4986, '风花之潇洒，雪月之空清，唯静者为之主；水木之荣枯，竹石之消长，独闲者操其权。', 9, '2020-02-12 16:32:31', '2020-04-23 16:02:03');
INSERT INTO `hitokoto` VALUES (4987, '死亡是唯一一座永远亮着的灯塔，不管你向哪里航行，最终都得转向它指引的方向。一切终会逝去，只有死神永生。', 4, '2020-02-12 16:31:31', '2020-04-23 16:17:20');
INSERT INTO `hitokoto` VALUES (4988, '黄油嘴边讲，纯爱心中藏。废萌永不倒，甜作放光芒。', 3, '2020-02-12 16:28:54', '2020-04-23 16:11:15');
INSERT INTO `hitokoto` VALUES (4989, '我们终会相遇相知，在那悠远的苍穹。', 1, '2020-02-12 16:27:18', '2020-04-23 16:09:04');
INSERT INTO `hitokoto` VALUES (4990, '一颗红心两只手，世世代代跟党走。', 4, '2020-02-12 15:41:57', '2020-04-23 16:02:42');
INSERT INTO `hitokoto` VALUES (4991, 'Star Burst Stream!', 1, '2020-02-12 13:53:29', '2020-04-23 16:24:59');
INSERT INTO `hitokoto` VALUES (4992, '不幸的人最怕别人说他们的幸福。', 4, '2020-02-12 10:10:53', '2020-04-23 16:15:14');
